INSERT INTO `teacher` VALUES ('384', 'Mariana', 'Pandi', 'mariana-pandi', '/static/upload/images/teacher/3-2016/HN/MarianaPandi.jpg', '', 'Hi, My name is Mariana, I am from Greece.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) BAHons in English Literature and Film</p>\r\n', '', '', 'GR', '', '0000-00-00 00:00:00', '<p>\r\n	I joined ILA after I completed my CELTA. I teach young learners including jumpstarts, as well as general English to adult learners. My teaching style is an interactive and communicative one with friendly approach in the classroom. I create a comfortable classroom environment so that students feel relaxed about practicing new language.</p>\r\n', '', '1', '2016-03-30 15:30:02');
INSERT INTO `teacher` VALUES ('385', 'Adi-Raj', 'Drungilas', 'adi-raj-drungilas', '/static/upload/images/teacher/3-2016/HN/AdiRaj.jpg', '', 'Hi, I am Adi. I am from Australia.', '', '<p>\r\n	Teach English To Foreign languages Certìicate (TEFL)</p>\r\n', '', '', 'AU', '', '0000-00-00 00:00:00', '<p>\r\n	I have recently started teaching English. I joined ILA after completing my teaching certificate.</p>\r\n', '', '1', '2016-03-30 15:31:17');
