INSERT INTO `teacher` VALUES ('403', 'Kathleen', 'El-Jeddi', 'kathleen-el-jeddi', '/static/upload/images/teacher/5-2016/Kathleen.jpg', '', 'Hi! My name is Kathleen. I am from America.', '', '<p>\r\n	CELTA BA in History (Gonzaga University)</p>\r\n', '', '', 'US', '', '0000-00-00 00:00:00', '<p>\r\n	I have been teaching English for 14 years. I have lived and worked in many countries including Malaysia, Yemen, and Libya. I am fun and creative and want everyone to have a good time in class.</p>\r\n', '', '1', '2016-05-09 12:48:22');
