INSERT INTO `teacher` VALUES ('439', 'Matthew', 'Davies', 'matthew-davies', '/static/upload/images/teacher/7-2016/Matthew-Davies.jpg', '', 'Hi My name\\\'s Matt and I\\\'m from Norwich in the United Kingdom.', '', '<p>\r\n	BA Hons English Literature (University of Lincoln) Certificate of English Language Teaching to Adults (CELTA)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	For two years I taught English to foreign students at a high school in the UK. After teaching in the UK I moved to Tokyo and taught English for two years. Vietnam has always been a country I\\&#39;ve wanted to teach in and I\\&#39;m very excited to be here.</p>\r\n', '', '1', '2016-06-20 16:39:17');
