INSERT INTO `teacher` VALUES ('461', 'Liam', 'Carr', 'liam-carr', '/static/upload/images/teacher/22-8-2016/Liam-Carr.jpg', '/static/upload/images/teacher/22-8-2016/Liam-Carr.jpg', 'Hi, My name is Liam, I am from the U.K.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Young Learner Extension to the Certificate in Teaching English to Speakers of Other Language (YLECELTA) B.A [University of Derby - U.K)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since early 2016. I have spent time teaching migrants in Newcastle upon Tyne, U.K My teaching style is an interactive one, whereby I also create a relaxed and friendly environment where students feel comfortable in furthering their learning.</p>\r\n', '', '1', '2016-08-22 17:37:25');
INSERT INTO `teacher` VALUES ('462', 'Jack', 'Henton', 'jack-henton', '/static/upload/images/teacher/22-8-2016/Jack-Hanton.jpg', '/static/upload/images/teacher/22-8-2016/Jack-Hanton.jpg', 'Hi, my name is Jack. I am from Great Britain. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) BA in English Literature (University of Southampton) ILA Young Learners Certificate (ILA YL)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA after completing my CELTA at International House London. My teaching style is an interactive one. I have a friendly approach in the classroom, and work to create a comfortable learning environment so that students feel confident and relaxed practicing new language.</p>\r\n', '', '1', '2016-08-22 17:40:19');
INSERT INTO `teacher` VALUES ('463', 'Joshua', 'Boam', 'joshua-boam', '/static/upload/images/teacher/22-8-2016/Josh-Boam.jpg', '/static/upload/images/teacher/22-8-2016/Josh-Boam.jpg', 'Hi, my name is Josh. I am from Britain. Very pleased to meet you!!', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA). ILA Young Learners Certificate (ILA YLC). BA in English (Aberystwyth University). PTLLS (Preparing To Teach In The Lifelong Learning Sector).</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2012. I joined ILA after completing my ILA YLC. I teach young learners including Jumpstarts, as well as general English to adult learners. My teaching style focuses on building trust and rapport with my students as well as developing both their confidence and English-speaking accuracy.</p>\r\n', '', '1', '2016-08-22 17:41:49');
INSERT INTO `teacher` VALUES ('464', 'Phillip', 'Bedford', 'phillip-bedford', '/static/upload/images/teacher/22-8-2016/Phillip-Bedford.jpg', '/static/upload/images/teacher/22-8-2016/Phillip-Bedford.jpg', 'Hi! My name is Phillip. I am from the United States of America. ', '', '<p>\r\n	Certificate of Teaching English as a Second Language (TEFL/TESOL) Certificate of Business English Young Learners Extension to the Certificate in Teaching English to Speakers of Other Languages (YLECELTA) B.A. in International Studies, Economics, University of Oklahoma, United States</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I have taught high school science since 2014. I joined ILA after completing my TEFL in 2016. My teaching style is interactive. I have a friendly approach in the classroom. I work to create a comfortable classroom environment so that students feel relaxed about practicing a new language.</p>\r\n', '', '1', '2016-08-22 17:42:06');
INSERT INTO `teacher` VALUES ('465', 'Levi', 'Tran', 'levi-tran', '/static/upload/images/teacher/22-8-2016/Levi-Tran.jpg', '/static/upload/images/teacher/22-8-2016/Levi-Tran.jpg', 'Hi, My name is Levi.  I am from America.  ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Young Learner Extension to the Certificate in Teaching English to Speakers of Other Languages (YLECELTA) A.S. [New England Institute of Technology - USA] B.A. [La Sierra University - USA] M.A. [Loma Linda University - USA]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I joined ILA after completing my CELTA. Before coming to Vietnam I worked in Occupational Therapy in the USA. I like my classes to be comfortable and engaging.</p>\r\n', '', '1', '2016-08-22 17:51:09');
INSERT INTO `teacher` VALUES ('467', 'John', 'Edmunds', 'john-edmunds', null, null, 'Hello everyone!  I am an English teacher from the USA and am excited to be teaching here in Vietnam.  I have lots of experience and look forward to a nice school year.', null, 'B.A. Political Science, Guilford College\r\nB.A German Studies, Guilford College\r\nTESOL Certification, The University of California, Santa Cruz', null, null, 'US', null, null, 'I have 6 years experience teaching ESL and US history,  I taught in the US public schools for 4 years, Austria for one year, and now in Vietnam a little more than a year. In my free time I like playing football (soccer) and reading.     ', null, '0', '2016-09-04 18:46:52');
INSERT INTO `teacher` VALUES ('468', 'Shannon', 'Feeney', 'shannon-feeney', null, null, 'Education for Life! ', null, ' Masters Degree from New York USA ', null, null, 'VN', null, null, 'I love teaching English to students of all ages!  I hope you\\\'ll be my student! ', null, '0', '2016-09-05 11:10:55');
INSERT INTO `teacher` VALUES ('469', 'Luke', 'Thurston', 'luke-thurston', '/static/upload/images/teacher/9-2016-v1/luke.jpg', '/static/upload/images/teacher/9-2016-v1/luke.jpg', 'I\\\'m Luke and I\\\'m from Birmingham in the UK', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Langauages (CELTA) BA Hons English &amp; History (Sheffield Hallam)</p>\r\n', '', null, 'VN', null, null, '<p>\r\n	I am a brand new EFL teacher having completed my CELTA in July 2016. Before that I was a Marketing Executive for 4 years. I hope to become a great teacher and am looking forward exploring Vietnam.</p>\r\n', '', '1', '2016-09-05 17:55:24');
INSERT INTO `teacher` VALUES ('470', 'Ana', 'Zuralovic', 'ana-zuralovic', '/static/upload/images/teacher/9-2016-v1/Ana-Zuralovic.jpg', '/static/upload/images/teacher/9-2016-v1/Ana-Zuralovic.jpg', 'Hi, my name is Ana. I am from Lithuania, Europe. ', '', '<p>\r\n	- CELTA (Cambridge Certificate in Teaching English to Adults); - BA in English Philology (Lithuanian University of Educational Sciences).</p>\r\n', '', null, 'LT', null, null, '<p>\r\n	I teach Jumpstarts, Super Juniors and Smart Teens. Language learning is like a muscle; the more you exercise it, the stronger it gets.</p>\r\n', '', '1', '2016-09-05 17:58:25');
INSERT INTO `teacher` VALUES ('471', 'Andrew', 'Bullough', 'andrew-bullough', '/static/upload/images/teacher/9-2016-v1/Andrew-Bullough.jpg', '/static/upload/images/teacher/9-2016-v1/Andrew-Bullough.jpg', 'Hey! My name is Andy from Shrewsbury in England. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English for 2 years. I have taught in Spain and Ecuador and am now looking forward to teaching here in Vietnam!</p>\r\n', '', '1', '2016-09-05 18:11:23');
INSERT INTO `teacher` VALUES ('472', 'Stuart', 'Dobson', 'stuart-dobson', '/static/upload/images/teacher/9-2016-v1/Stuart-Dobson.jpg', '/static/upload/images/teacher/9-2016-v1/Stuart-Dobson.jpg', 'Hi, My name Is Stuart. I\'m from England.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2007. I have worked in Russia, the Czech Republic, Turkey and Azerbaijan. I teach young learners, general English and exam preparation. I have an energetic teaching style and want a happy atmosphere in my classroom. I want my students to be relaxed and confident.</p>\r\n', '', '1', '2016-09-05 18:11:58');
INSERT INTO `teacher` VALUES ('473', 'Alexander ', 'Hopkins', 'alexander-hopkins', '/static/upload/images/teacher/9-2016-v1/Alex-Hopkins.jpg', '/static/upload/images/teacher/9-2016-v1/Alex-Hopkins.jpg', 'Hey, my name is Alex, I\'m from England. ', '', '<p>\r\n	Certified CELTA young learners extension from ILA Masters, University of barcelona</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English for a year! I joined ILA this year. I teach English to a range of levels and ages in a fun and friendly manner.</p>\r\n', '', '1', '2016-09-05 18:12:44');
INSERT INTO `teacher` VALUES ('474', 'Khalim', 'Khan-Whelan', 'khalim-khan-whelan', '/static/upload/images/teacher/9-2016-v1/Khalim-Khan-Whenlan.jpg', '/static/upload/images/teacher/9-2016-v1/Khalim-Khan-Whenlan.jpg', 'Hello, my name is Khalim and I am from England. I came to Vietnam in the summer of 2016 and I\'m looking forward to discovering this beautiful country.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Music, B.A Music</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA in September, 2016. I enjoy teaching young learners. I like to create a comfortable and relaxed environment in the classroom.</p>\r\n', '', '1', '2016-09-05 18:14:42');
INSERT INTO `teacher` VALUES ('475', 'Paul', 'Merwood', 'paul-merwood', '/static/upload/images/teacher/9-2016-v1/Paul-Merwood.jpg', '/static/upload/images/teacher/9-2016-v1/Paul-Merwood.jpg', 'Hello. My name is Paul and I\\\'m from England', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A Honours (University of Kent at Canterbury, England)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	This will be my first English teaching job since completing my CELTA course in 2015. I\\&#39;m excited to be working for ILA and the opportunity to teach young learners. I hope to create fun and interactive lessons that everybody can enjoy, in a classroom where students feel relaxed and motivated</p>\r\n', '', '1', '2016-09-05 18:15:15');
INSERT INTO `teacher` VALUES ('476', 'Rebecca', 'Levy', 'rebecca-levy', '/static/upload/images/teacher/9-2016-v1/Rebecca-Levy.jpg', '/static/upload/images/teacher/9-2016-v1/Rebecca-Levy.jpg', 'Hello, my name is Rebecca. I am from Scotland in the U.K.', '', '<p>\r\n	- Certificate in Teaching English to Speakers of Other Languages. (CertTESOL) - Young Learner Extension to the Certificate in Teaching English to Speakers of Other Languages (YLCertTESOL) - B.A French [University of Glasgow - Scotland U.K.]</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2014. I carried out my CertTESOL in Barcelona and worked there for two years before coming to Vietnam. I enjoy young learners as well as general English adults. I like creating fun, interactive lessons for my students. I enjoy getting to know my students and encouraging them to feel comfortable learning English in my class.</p>\r\n', '', '1', '2016-09-05 18:19:34');
INSERT INTO `teacher` VALUES ('477', 'Tracy', 'Hinton', 'tracy-hinton', '/static/upload/images/teacher/9-2016-v1/Tracy-Hinton.jpg', '/static/upload/images/teacher/9-2016-v1/Tracy-Hinton.jpg', 'Hi, my name is Tracy.', '', '<p>\r\n	CELTA BA Hons English PGCE Post-16</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English for 10 years. I have also taught English as a Foreign Language in Tanzania and in South America.</p>\r\n', '', '1', '2016-09-05 18:25:54');
INSERT INTO `teacher` VALUES ('478', 'Elizabeth', 'Foster', 'elizabeth-foster', '/static/upload/images/teacher/9-2016-v1/Elizabeth-foster.jpg', '/static/upload/images/teacher/9-2016-v1/Elizabeth-foster.jpg', 'Hi, my name is Liz. I am from the United Kingdom.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) BA (Hon) English Literature at Lancaster University, UK.</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I completed a CELTA in January 2016 and joined ILA in September 2016.</p>\r\n', '', '1', '2016-09-05 18:26:50');
INSERT INTO `teacher` VALUES ('479', 'Rosemary', 'Anstead', 'rosemary-anstead', '/static/upload/images/teacher/9-2016-v1/Rosemary-Anstead.jpg', '/static/upload/images/teacher/9-2016-v1/Rosemary-Anstead.jpg', 'Hello, my name is Rose. I am from the UK.', '', '<p>\r\n	TEFL certificate in teaching english as a foreign language B.A Honors from Cardiff</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I am new to Hanoi and teaching english. I absolutely love it! I teach young learners including Juniors and Smart Teens and like to bring an energetic energy to the class room.</p>\r\n', '', '1', '2016-09-06 15:37:46');
INSERT INTO `teacher` VALUES ('480', 'Linh', 'Lam', 'linh-lam', '/static/upload/images/teacher/9-2016-v1/Linh-Lam.jpg', '/static/upload/images/teacher/9-2016-v1/Linh-Lam.jpg', 'Hi my name is Linh and I am from Canada. My parents are from Vietnam, and I decided to come back to their home country to teach English and to explore the beautiful country. I gained a passion for teaching English after having taken care of kids over the past years. I have obtained my TESOL certificate in Ho Chi Minh city at the Australian- Vietnam School of English. I am excited to continue teaching and to apply my skills and knowledge in English to ILA-Hanoi. ', '', '<p>\r\n	Certificate in TESOL - Australian Vietnam School of English BA Honours in Commerce-Management Carleton University, Ottawa, Canada</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	Hi my name is Linh and I am from Canada. My parents are from Vietnam, and I decided to come back to their home country to teach English and to explore the beautiful country. I gained a passion for teaching English after having taken care of kids over the past years. I have obtained my TESOL certificate in Ho Chi Minh city at the Australian- Vietnam School of English. I am excited to continue teaching and to apply my skills and knowledge in English to ILA-Hanoi.</p>\r\n', '', '1', '2016-09-06 15:39:34');
INSERT INTO `teacher` VALUES ('481', 'Lauren', 'Naismith', 'lauren-naismith', '/static/upload/images/teacher/9-2016-v1/Lauren-Naismith.jpg', '/static/upload/images/teacher/9-2016-v1/Lauren-Naismith.jpg', 'Hi, my name is Lauren. I am both South African and British. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A (University of South Africa- S.A)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching since 2014. I have taught in Bangkok, Thailand and now Vietnam. I love teaching young learners. My teaching style is an interactive one. I am an extremely friendly,happy and enthuastic approach in the classroom.</p>\r\n', '', '1', '2016-09-06 15:42:30');
INSERT INTO `teacher` VALUES ('483', 'Will', 'McCandless', 'will-mccandless', null, null, 'Hi, my name is Will. I\\\'m from ATL, Georgia, USA.', null, 'CELTA\r\nILA YL', null, null, 'US', null, null, 'I\\\'ve been teaching for 4 years: 2 years in Korea, 2 years in Vietnam. I did my CELTA in Atlanta. I am currently doing an MA TESOL course online. I like mid-to-high level teens classes.', null, '0', '2016-09-09 16:14:49');
INSERT INTO `teacher` VALUES ('484', 'Rachael', 'Haywood', 'rachael-haywood', '/static/upload/images/teacher/9-2016-v2/Rachael.jpg', '/static/upload/images/teacher/9-2016-v2/Rachael.jpg', 'Hi. My name is Rachael. I am from England. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages ̣- CELTA BA in Philosophy</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I am really excited to be working at ILA. I joined ILA after completing my CELTA and am passionate about teaching. I enjoy creating communicative activities which are interactive and engaging.</p>\r\n', '', '1', '2016-09-19 17:40:27');
INSERT INTO `teacher` VALUES ('485', 'Deirdre', 'Jordan', 'deirdre-jordan', '/static/upload/images/teacher/9-2016-v2/Dee.jpg', '/static/upload/images/teacher/9-2016-v2/Dee.jpg', 'Hello. My name is Dee and I am from Ireland.', '', '<p>\r\n	Certificate in English Language Teaching. BA in Social Care Applied Social Studies.</p>\r\n', '', null, 'IE', null, null, '<p>\r\n	I have 2 years teaching experience in Spain and really enjoyed my time there. I work to create a comfortable environment in the classroom and want my students to enjoy their time with me. I am excited to begin teaching in Vietnam.</p>\r\n', '', '1', '2016-09-19 17:40:48');
INSERT INTO `teacher` VALUES ('486', 'Emma', 'Ronney', 'emma-ronney', '/static/upload/images/teacher/9-2016-v2/emma.jpg', '/static/upload/images/teacher/9-2016-v2/emma.jpg', 'My name is Emma. I am from Scotland.', '', '<p>\r\n	Certificate in Teaching English to speakers of other Languages (CELTA) BA (Hons) - Film and Media</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I completed by Celta in June 2016. I have taught international young learners over the summer in schools within the UK. I am passionate about teaching young learners and truly enjoy doing it. I am warm, friendly and approachable and create a comfortable learning environment to ensure my students get the most out of their lessons.</p>\r\n', '', '1', '2016-09-19 17:42:38');
INSERT INTO `teacher` VALUES ('487', 'Adam', 'Browne', 'adam-browne', '/static/upload/images/teacher/9-2016-v2/Adam.jpg', '/static/upload/images/teacher/9-2016-v2/Adam.jpg', 'Hi, my name is Adam. I am from England', '', '<p>\r\n	Certificate in English Language Teaching BA (Hons) York St John University, United Kingdom</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2012. I teach Young learners and Adults. My teaching style is interactive and focuses on learners as individuals. I like to use creativity and technology in the classroom.</p>\r\n', '', '1', '2016-09-19 17:43:05');
INSERT INTO `teacher` VALUES ('488', 'Josh', 'Hanson', 'josh-hanson', '/static/upload/images/teacher/9-2016-v2/Josh.jpg', '/static/upload/images/teacher/9-2016-v2/Josh.jpg', 'Hey, My name is Josh. I\\\'m from the UK. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A. English (University College London - U.K.)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I\\&#39;ve been teaching English since 2012. I\\&#39;ve taught in Georgia, Italy, Indonesia, Kyrgyzstan and the UK. I like to play lots of games in my lessons and use media such as film and music to keep my students engaged.</p>\r\n', '', '1', '2016-09-19 17:47:28');
INSERT INTO `teacher` VALUES ('489', 'Fay', 'Powell', 'fay-powell', '/static/upload/images/teacher/9-2016-v2/Fay.jpg', '/static/upload/images/teacher/9-2016-v2/Fay.jpg', 'Hello, my name is Fay and I\\\'m from the UK.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (TESOL - Trinity) BA in Drama with English (Loughborough University)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English for 3 years in the UK and Italy and now luckily Vietnam. I love teaching both Young Learners and adults and enjoy making learning fun and creative. I create a friendly atmosphere for my students and help them to feel confident when learning English.</p>\r\n', '', '1', '2016-09-19 17:54:31');
INSERT INTO `teacher` VALUES ('490', 'Liubov', 'Kuznetsova', 'liubov-kuznetsova', '/static/upload/images/teacher/9-2016-v2/Luba.jpg', '/static/upload/images/teacher/9-2016-v2/Luba.jpg', 'Hi, My name is Luba. I am from Russia.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA), New York, USA B.A. - Kostroma State Pedagogical University, Kostroma, Russia</p>\r\n', '', null, 'RU', null, null, '<p>\r\n	Before I moved to Vietnam, I studied and worked in the United States for a long time. I joined ILA after completing my CELTA. I teach young learners including Jumpstarts, as well as general English to adult learners. I use effective and efficient methods of teaching, while focusing on the individual needs of each student</p>\r\n', '', '1', '2016-09-19 18:04:59');
INSERT INTO `teacher` VALUES ('491', 'Thomas', 'Hills', 'thomas-hills', '/static/upload/images/teacher/10-2016(v1)/Thomas-Hill.jpg', '/static/upload/images/teacher/10-2016(v1)/Thomas-Hill.jpg', 'Hi my name is Tom. I am from the United Kingdom.', '', '<p>\r\n	TEFL 140 hour Bsc Politics and International Relations - The University of Bath</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I am a new and open minded teacher ready to learn and grow along with my students at ila.</p>\r\n', '', '1', '2016-10-10 17:02:37');
INSERT INTO `teacher` VALUES ('492', 'Thomas', 'Barrett', 'thomas-barrett', '/static/upload/images/teacher/10-2016(v1)/Thomas-Barrett.jpg', '/static/upload/images/teacher/10-2016(v1)/Thomas-Barrett.jpg', 'Hi. My name is Thomas Barrett, and I\\\'m from Harrogate in the U.K.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A. - English Literature - Leeds Beckett University M.A - English Literature - The University of Leeds</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I am passionate about the English language, and love poetry and wordplay - and look forward to bringing some of this experience to the classroom. I have spent the past 5 years working at a Montessori school in the U.K, working with young learners. Coming to Vietnam is a massive adventure and I intend to enjoy every moment of it.</p>\r\n', '', '1', '2016-10-10 17:03:23');
INSERT INTO `teacher` VALUES ('493', 'chanmeet', 'kohli', 'chanmeet-kohli', '/static/upload/images/teacher/10-2016(v1)/Chan.jpg', '/static/upload/images/teacher/10-2016(v1)/Chan.jpg', 'Hi, My name is Chan. I\\\'m from London.', '', '<p>\r\n	Certificate in Teaching English of other Languages. (CELTA) BSc in Sport, Health and Exercise Science</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA in October 2016. My teaching styles are interactive, friendly and fun.</p>\r\n', '', '1', '2016-10-10 17:03:51');
INSERT INTO `teacher` VALUES ('494', 'Joseph', 'Yeshewas', 'joseph-yeshewas', '/static/upload/images/teacher/10-2016(v1)/Joshep-Yeshewar.jpg', '/static/upload/images/teacher/10-2016(v1)/Joshep-Yeshewar.jpg', 'Hi, my name is Joseph. I am from California.', '', '<p>\r\n	Certificate in Teaching English as a Foreign Language (TEFL) B.A. [University of California, Riverside]</p>\r\n', '', null, 'VN', null, null, '<p>\r\n	I have been teaching English since 2013. I joined ILA in October 2016. I teach young learners. My teaching style is an interactive one. I have a friendly approach in the classroom. I work to create a comfortable environment so that students feel relaxed about practicing new language.</p>\r\n', '', '1', '2016-10-10 17:06:01');
INSERT INTO `teacher` VALUES ('495', 'Stephan', 'Buijnsters', 'stephan-buijnsters', '/static/upload/images/teacher/10-2016(v1)/Stef-RuijnSters.jpg', '/static/upload/images/teacher/10-2016(v1)/Stef-RuijnSters.jpg', 'Hi, my name is Stef. I am from England', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A. International Business Management (University of Wales Institute)</p>\r\n', '', null, 'VN', null, null, '<p>\r\n	I have been teaching English since 2013. I joined ILA in October 2016. I teach young learners. My teaching approach is both interactive and communicative. I aim for students to practice new language in a comfortable environment.</p>\r\n', '', '1', '2016-10-10 17:06:31');
INSERT INTO `teacher` VALUES ('496', 'Dean', 'Taylor', 'dean-taylor', '/static/upload/images/teacher/10-2016(v1)/Dean-Taylor.jpg', '/static/upload/images/teacher/10-2016(v1)/Dean-Taylor.jpg', 'Hi, My name is Dean. I am from London, England.', '', '<p>\r\n	TEFL 120 Hours certificate. Computer Science BSc</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	This is my first teaching job. I have previously worked in technology as a Business Analyst.</p>\r\n', '', '1', '2016-10-10 17:06:33');
INSERT INTO `teacher` VALUES ('497', 'Shraddha', 'Srivastava', 'shraddha-srivastava', '/static/upload/images/teacher/10-2016(v1)/Shraddha-Srivastava.jpg', '/static/upload/images/teacher/10-2016(v1)/Shraddha-Srivastava.jpg', 'Hi! I am Shraddha from India.', '', '<p>\r\n	CELTA MA in English Language Teaching, University of Sussex, UK</p>\r\n', '', null, 'IN', null, null, '<p>\r\n	I have been teaching English for over four years. I have taught students from across the world and I also have a wide experience teaching English online to adults and young learners.</p>\r\n', '', '1', '2016-10-10 17:06:36');
INSERT INTO `teacher` VALUES ('498', 'Larkyn', 'Froese', 'larkyn-froese', '/static/upload/images/teacher/10-2016(v1)/Larkyn-Froese.jpg', '/static/upload/images/teacher/10-2016(v1)/Larkyn-Froese.jpg', 'Hi, My name is Larkyn. I am from Canada.', '', '<p>\r\n	-Certificate in Teaching English to speakers of other languages. (CELTA) -BA [Queen\\&#39;s University in Kingston, Ontario]</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	I joined ILA after completing my CELTA. In Canada I taught art classes.</p>\r\n', '', '1', '2016-10-10 17:06:57');
INSERT INTO `teacher` VALUES ('499', 'Eren', 'Emin', 'eren-emin', '/static/upload/images/teacher/10-2016(v1)/Eren-Emin.jpg', '/static/upload/images/teacher/10-2016(v1)/Eren-Emin.jpg', 'Hello, my name is Eren and I am from London.', '', '<p>\r\n	Certificate in Teaching English as a foreign Language (TEFL). B.A Hons Criminology and criminal justice.</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	This is my first time teaching English and I am very excited to be with ILA to develop my teaching skills.</p>\r\n', '', '1', '2016-10-10 17:07:17');
INSERT INTO `teacher` VALUES ('500', 'Matthew', 'Coster', 'matthew-coster', '/static/upload/images/teacher/10-2016(v1)/Masthew-Coster.jpg', '/static/upload/images/teacher/10-2016(v1)/Masthew-Coster.jpg', 'Hello! I\\\'m Matthew from England.', '', '<p>\r\n	Certificate in English Language Teaching for Adults (CELTA) Masters Degree in Teaching English to Speakers of Other Languages (TESOL) from University College London Bachelors Degree in History and English from University of Manchester</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I\\&#39;ve been teaching English since 2012 after completing my CELTA. After teaching in Thailand and South Korea I went back to England to study for a Masters and train to be a better teacher! I\\&#39;m excited to be in Vietnam and teaching Vietnamese students. I expect my students to work hard with a positive attitude, because they can always rely on me for the same.</p>\r\n', '', '1', '2016-10-10 17:07:23');
INSERT INTO `teacher` VALUES ('501', 'Jill', 'Hetherington', 'jill-hetherington', '/static/upload/images/teacher/10-2016(v1)/Jill-Hetherington.jpg', '/static/upload/images/teacher/10-2016(v1)/Jill-Hetherington.jpg', 'My name is Jill. I am Australian but I come from the UK.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) ILA Young Learners (ILAYL)</p>\r\n', '', null, 'VN', null, null, '<p>\r\n	I have been teaching English and French for over 30 years. I joined ILA after completing my CELTA. I like learners to be actively involved in all parts of the lesson and to enjoy their learning. A good lesson is when learners leave the lesson knowing more than they did when they arrived.</p>\r\n', '', '1', '2016-10-10 17:08:33');
INSERT INTO `teacher` VALUES ('502', 'Elizabeth Anne', 'Keyser', 'elizabeth-anne-keyser', null, null, 'My name is Liz.  I am from Cape Town, South Africa.', null, 'Certificate in Teaching English to Speakers of Other Languages (CELTA)\r\nTEFL Masters\r\nBA (English, Psychology, Criminology)\r\nDiploma in Remedial Education (Dip Rem Ed)\r\nILA Young Learners (ILAYL)', null, null, 'ZA', null, null, 'I am a qualified Primary School teacher with 16 years of teaching experience in South Africa in mainstream primary schools and in a remedial school. After running my own business for 20 years, I completed an online TEFL and my CELTA and did two months of part time teaching at various adult language centres in Cape Town.  My passion is teaching and I am so excited and privileged to be able to follow this dream of assisting, guiding and teaching students to master the English language.', null, '0', '2016-10-10 17:09:10');
INSERT INTO `teacher` VALUES ('503', 'Richard', 'Naughton', 'richard-naughton', '/static/upload/images/teacher/10-2016(v1)/Richard-Naughton.jpg', '/static/upload/images/teacher/10-2016(v1)/Richard-Naughton.jpg', 'Hi my name is Richie, I am from England!', '', '<p>\r\n	- Teaching English as a Second Other Language (TESOL) - B.A. Geography (University of Hull)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA after completing my TESOL. I teach young learners ranging from Jumpstarters to Young Adults here at Centre 2. I like to consider myself to be an engaging teacher whose emphasis is on interaction with the students. My goal is to create a comfortable environment for effective development.</p>\r\n', '', '1', '2016-10-10 17:09:44');
INSERT INTO `teacher` VALUES ('504', 'Pat', 'Duggan', 'pat-duggan', '/static/upload/images/teacher/10-2016(v1)/Pat-Duggan.jpg', '/static/upload/images/teacher/10-2016(v1)/Pat-Duggan.jpg', 'Hi.  My name is Pat and I am from Australia.  I am excited to be working at ILA!  I\\\'m very much looking forward to working with young people in Vietnam, I\\\'m quite passionate about helping them to improve their opportunities in a rapidly growing economy.', '', '<p>\r\n	I have a CELTA qualification, and I am 3/4 of my way through a Graduate Certificate in TESOL, started this year. I have a masters degree in international development.</p>\r\n', '', null, 'AU', null, null, '<p>\r\n	I have worked in international aid and development in Asia, Africa and the Pacific over the last 3 decades. I cant think of a better country to begin my new life chapter as a TESOL teacher!</p>\r\n', '', '1', '2016-10-10 17:13:02');
INSERT INTO `teacher` VALUES ('505', 'Andrew', 'Mylett', 'andrew-mylett', '/static/upload/images/teacher/10-2016(v1)/Adrew-Mylett.jpg', '/static/upload/images/teacher/10-2016(v1)/Adrew-Mylett.jpg', 'Hi, my name is Andrew from London in the United Kingdom.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Contemporary Political Theory (MSc) Politics (BA Hons)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching with the ILA since October 2016. I have taught Business English in Lille, France and in Perth, Australia. My teaching style is interactive and student focused. I create a fun and relaxed environment where students can express themselves freely and enjoy learning with their peers.</p>\r\n', '', '1', '2016-10-10 17:13:33');
INSERT INTO `teacher` VALUES ('506', 'Tomas', 'Kocourek', 'tomas-kocourek', '/static/upload/images/teacher/10-2016(v1)/Tom-Kocourek.jpg', '/static/upload/images/teacher/10-2016(v1)/Tom-Kocourek.jpg', 'Hi, my name\\\'s Tomas but I prefer Tom. I come from a small town near Prague, Czech Republic. My hobbies include sport, art, cooking, and, of course, teaching English. ', '', '<p>\r\n	CELTA MA [Masaryk University, Brno, Czech Rep.]</p>\r\n', '', null, 'CZ', null, null, '<p>\r\n	I\\&#39;ve been teaching English as a foreign language since 2008. So far, I have mainly taught business English and ESP. I joined ILA upon completion of my CELTA. Now I also teach young learners including Jumpstarts. I am a patient, supportive, and motivating teacher. My students are my clients as well as my friends. I work to create a friendly classroom environment to make all students feel welcome and safe when learning.</p>\r\n', '', '1', '2016-10-10 17:14:29');
INSERT INTO `teacher` VALUES ('507', 'Storm', 'Langley', 'storm-langley', '/static/upload/images/teacher/10-2016(v1)/Storm-Langley.jpg', '/static/upload/images/teacher/10-2016(v1)/Storm-Langley.jpg', 'Hi, my name is Storm. I am from South Africa.', '', '<p>\r\n	- Certificate Teaching English to Speakers of Other Languages (CertTESOL) - B.A [CTI Education Group - SA]</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I teach various age groups and focus on making my lessons being fun and engaging. I am excited to get started with ILA and look forward to getting to know my students and learning with them.</p>\r\n', '', '1', '2016-10-10 17:16:21');
INSERT INTO `teacher` VALUES ('508', 'Gully', 'Makin', 'gully-makin', '/static/upload/images/teacher/10-2016(v2)/Gully-Makin.jpg', '/static/upload/images/teacher/10-2016(v2)/Gully-Makin.jpg', 'Hello, my name is Gulliver. I am from Leicester in England and have lived in Prague and Bangkok.', '', '<p>\r\n	TESOL- 120 hours [Oxford House Prague] BA HONS Politics [Manchester Metropolitan University]</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have taught in both Asia and Europe working for GLOSSA and Wall Street English. I have previously taught ages 12-82 general English, Conversation and business English. My lesson aim is for them to be fun, dynamic and interactive so I hope that you enjoy them!</p>\r\n', '', '1', '2016-10-24 17:14:32');
INSERT INTO `teacher` VALUES ('509', 'Scott', 'Bahlmann', 'scott-bahlmann', '/static/upload/images/teacher/10-2016(v2)/Scott-Bahlmmann.jpg', '/static/upload/images/teacher/10-2016(v2)/Scott-Bahlmmann.jpg', 'Hi, I\'m Scott from America.   ', '', '<p>\r\n	-Certificate in Teaching English to Speakers of Other Languages (CELTA) -Young Learners Extension to the Certificate in Teaching English to Speakers of Other Languages (YLECELTA) -B S Theatre Arts [Southern Utah University, Cedar City - U.S.A.] -English and Literature Sterling Scholar [Manti High School, Manti - U.S.A.]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I took my CELTA course with ILA, and enjoy the school so much I took a teaching contract with them. I spent three years as a children\\&#39;s librarian and am excited to teach young learners. I feel that every student is unique and deserves attention.</p>\r\n', '', '1', '2016-10-24 17:14:37');
INSERT INTO `teacher` VALUES ('510', 'Jeremy', 'Lovemore', 'jeremy-lovemore', '/static/upload/images/teacher/10-2016(v2)/Jeremy-Lovemore.jpg', '/static/upload/images/teacher/10-2016(v2)/Jeremy-Lovemore.jpg', 'Hi, my name is Jeremy. I am from Cape Town, South Africa. ', '', '<p>\r\n	- BCOM (Information Systems Management and Entrepreneurship &amp; Innovation Management) - Teaching English to Students of Other Languages (TESOL)</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I am a recent graduate and started my teaching career in August 2016. I joined ILA in October 2016. I teach young learners including Jumpstarts as well as general English to adult learners. My teaching approach relies on being patient, clear and comprehensive. This allows my demeanor to be both friendly and animated which creates a relaxed and comfortable classroom environment.</p>\r\n', '', '1', '2016-10-24 17:18:13');
INSERT INTO `teacher` VALUES ('511', 'JT', 'Barta', 'jt-barta', '/static/upload/images/teacher/10-2016(v2)/JT-Barta.jpg', '/static/upload/images/teacher/10-2016(v2)/JT-Barta.jpg', 'Hello, my name is JThomas, but I go by JT! I\\\'m from the United States.', '', '<p>\r\n	-B.A - Music/Computer Science - Tulane University of New Orleans - United States -TESOL/TESL/TEFL Certification - Oxford Seminars -Certificate in Teaching English to Speakers of Other Languages (CELTA)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I left university with the goal of teaching English and Music simultaneously. I joined ILA shortly after completing my CELTA. I have worked with many Vietnamese students in preparation for IELTS/TOEFL Exams as well as Visa interviews to study abroad. I do my very best to engage all learners and maintain a comfortable environment for English learning. I also like to challenge students to push themselves and their peers to do their very best.</p>\r\n', '', '1', '2016-10-24 17:19:01');
INSERT INTO `teacher` VALUES ('512', 'Daniel', 'Erasmus', 'daniel-erasmus', '/static/upload/images/teacher/10-2016(v2)/Daniel-Erasmus.jpg', '/static/upload/images/teacher/10-2016(v2)/Daniel-Erasmus.jpg', 'Hi, my name is Daniel. I am from Cape Town, South Africa. ', '', '<p>\r\n	-BA Humanities (Psychology and English) -Teaching English to Speakers of Other Languages (TESOL)</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	- I started teaching English after completing my university degree in 2016. - I joined ILA after completing my TESOL. - I teach English to Jumpstarts, as well as general English to older learners. I recently moved to Vietnam and am loving it. The culture, people and food are some of my favourite things about the country. I am an enthusiastic teacher that enjoys class interaction. I enjoy creating a comfortable learning atmosphere that allows my students to express themselves and have fun.</p>\r\n', '', '1', '2016-10-24 17:19:16');
INSERT INTO `teacher` VALUES ('513', 'Kamila', 'Zjawin', 'kamila-zjawin', '/static/upload/images/teacher/10-2016(v2)/Kamila-Zjawin.jpg', '/static/upload/images/teacher/10-2016(v2)/Kamila-Zjawin.jpg', 'Hello! My name is Kamila and I\\\'m from Canada.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (TEFL) B.A from Wilfrid Laurier University, Waterloo, Ontario</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	I am new to Vietnam and I\\&#39;m excited to be teaching at ILA! I can\\&#39;t wait to work with students of all ages and help them discover a passion for English.</p>\r\n', '', '1', '2016-10-24 17:19:50');
INSERT INTO `teacher` VALUES ('514', 'Nahid', 'Sadeghi', 'nahid-sadeghi', '/static/upload/images/teacher/10-2016(v2)/Nahid-Sadeghi.jpg', '/static/upload/images/teacher/10-2016(v2)/Nahid-Sadeghi.jpg', 'Hi everyone! My name is Nahid. I am really excited to  start working as a member of ILA Vietnam.', '', '<p>\r\n	M.A in Applied Linguistics Certificate in Teaching English to Speakers of Other Languages (CELTA) IELTS Teacher Training Certificate (TESOL Arabia)</p>\r\n', '', null, 'IR', null, null, '<p>\r\n	I have been teaching English to students from different cultural and educational backgrounds for 11 years now. I love communicative approaches of teaching and supporting students to be autonomous learners for real-life situations.</p>\r\n', '', '1', '2016-10-24 17:21:19');
INSERT INTO `teacher` VALUES ('515', 'MILES', 'SHIRLEY', 'miles-shirley', '/static/upload/images/teacher/11-2016/v1/Miles-Shirley.jpg', '/static/upload/images/teacher/11-2016/v1/Miles-Shirley.jpg', 'Hi. My name is Miles. I am from London, England.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Masters in Counselling &amp; Psychotherapy (MA) Bachelor in Philosophy (BA)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2010. I have taught all levels and all ages. I have taught in Vietnam and also Saudi Arabia.</p>\r\n', '', '1', '2016-11-14 11:11:22');
INSERT INTO `teacher` VALUES ('516', 'Rachel ', 'Waters', 'rachel-waters', '/static/upload/images/teacher/11-2016/v1/Rachel-water.jpg', '/static/upload/images/teacher/11-2016/v1/Rachel-water.jpg', 'Hi, My name is Rachel. I am from Australia.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages ̣CELTA Bachelor of Business ̣(Human Resource Management and Industrial Relations)</p>\r\n', '', null, 'AU', null, null, '<p>\r\n	After careers in communications and human resources I decided to re-train as a teacher, completing my CELTA certificate in September 2016. I joined ILA soon after. I like to create a fun and friendly atmosphere in class, giving each student opportunities for success.</p>\r\n', '', '1', '2016-11-14 11:14:48');
INSERT INTO `teacher` VALUES ('517', 'Justin', 'Fitzgerald', 'justin-fitzgerald', '/static/upload/images/teacher/11-2016/v1/Justin-Fitzgeracd.jpg', '/static/upload/images/teacher/11-2016/v1/Justin-Fitzgeracd.jpg', 'Hi, my name is Justin! I am from Vancouver, Canada, where the mountains meet the ocean and another Justin reigns supreme. ', '', '<p>\r\n	CELTA (Certificate in Teaching English to Speakers of Other Languages) - International House (Vancouver, Canada) B.A. in Political Science - Simon Fraser University (Burnaby, Canada) - University of Sussex (Brighton, UK) Post-Graduate Certificate in Public Service - Conestoga College Institute of Technology and Advanced Learning (Kitchener, Canada)</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	I started substitute teaching in Vancouver after completing CELTA in April, 2016. I moved to Vietnam to work for ILA in November, 2016. In the past I have also lived and studied in the UK, as well as Canada. My teaching style is student-centred and emphasizes student involvement and interaction, and harnessing new and interactive technologies to promote 21st century learning.</p>\r\n', '', '1', '2016-11-14 11:16:12');
INSERT INTO `teacher` VALUES ('518', 'Tom', 'Bogues', 'tom-bogues', '/static/upload/images/teacher/11-2016/v1/Tom-Bogues.jpg', '/static/upload/images/teacher/11-2016/v1/Tom-Bogues.jpg', 'Hi, my name is Tom. I am from Northern Ireland.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Postgraduate Diploma Construction and Project Management (Queen\\&#39;s University, Belfast) Bsc. Management (Queen\\&#39;s University, Belfast)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	Hi, I completed my CELTA in June 2016. I am excited to live and teach in Vietnam. I try to create a friendly and co-operative classroom where all my students feel comfortable.</p>\r\n', '', '1', '2016-11-14 11:16:23');
INSERT INTO `teacher` VALUES ('519', 'Ashley', 'Quinlan', 'ashley-quinlan', '/static/upload/images/teacher/11-2016/v1/Ashley-Quinlan.jpg', '/static/upload/images/teacher/11-2016/v1/Ashley-Quinlan.jpg', 'Hi, my name is Ashley. I am from the United States.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.S. Environmental Science from Loyola University Chicago (United States)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	After graduating from college, I moved to Vietnam to take my CELTA. I joined ILA after completing my CELTA. I teach young learners. I enjoy getting to know all of my students and creating a fun and engaging classroom.</p>\r\n', '', '1', '2016-11-14 11:16:24');
INSERT INTO `teacher` VALUES ('520', 'David', 'Bond', 'david-bond', '/static/upload/images/teacher/11-2016/v1/David-Bond.jpg', '/static/upload/images/teacher/11-2016/v1/David-Bond.jpg', 'Hello there, my name is David. I am from Liverpool, UK. ', '', '<p>\r\n	Bachelors Degree in Law (LLB) TESOL Certificate (Teaching English to Speakers of Other Languages) from Trinity College London.</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	After studying Law at the University of Sheffield I decided to train to become an English teacher. I worked in the UK as both an English Teacher and a Paralegal before joining ILA. I like to facilitate a friendly atmosphere in my classes that makes students comfortable with using new language.</p>\r\n', '', '1', '2016-11-14 11:18:41');
INSERT INTO `teacher` VALUES ('521', 'Katie', 'Bell', 'katie-bell', '/static/upload/images/teacher/11-2016/v1/Kah-Bell.jpg', '/static/upload/images/teacher/11-2016/v1/Kah-Bell.jpg', 'Hi! I am Katie and I am from London, England. ', '', '<p>\r\n	Certificate in Teaching English to speakers of Other Languages (CELTA) B.A English Literature and Modern History</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA after teaching in London. I teach young learners and adults with My teaching style is fun, friendly and fabulous!</p>\r\n', '', '1', '2016-11-14 11:20:51');
INSERT INTO `teacher` VALUES ('522', 'Tiffany', 'Nguyen', 'tiffany-nguyen', '/static/upload/images/teacher/11-2016/v1/Tiffany-Nguyen.jpg', '/static/upload/images/teacher/11-2016/v1/Tiffany-Nguyen.jpg', 'Hi! My name is Tiffany. I am from the United States.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.S. Biology, University of Notre Dame (United States)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I joined ILA after completing my CELTA. I teach young learners and adults. My teaching style is enthusiastic, energetic, and engaging. I have taught children of all ages in the classroom and the outdoors.</p>\r\n', '', '1', '2016-11-14 11:20:56');
INSERT INTO `teacher` VALUES ('523', 'Brian', 'Mackay', 'brian-mackay', '/static/upload/images/teacher/11-2016/v1/Brian-Mackay.jpg', '/static/upload/images/teacher/11-2016/v1/Brian-Mackay.jpg', 'Hello, I am Brian Mackay. I am from the USA, but live in Thủ Đức, HCMC.', '', '<p>\r\n	- Certificate in Teaching English to Speakers of Other Languages (CELTA) - Bachelors in Cultural Anthropology [California State University, Long Beach]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I\\&#39;ve been building my career in the field of education since 2012 and am constantly working to better my teaching skills for my students and myself. I am always excited for the next day here at ILA. Whether it\\&#39;s energizing young learners or empowering adults, every experience is one I treasure and one that I hope my students remember.</p>\r\n', '', '1', '2016-11-14 11:23:34');
INSERT INTO `teacher` VALUES ('524', 'Samuel', 'Milburn', 'samuel-milburn', '/static/upload/images/teacher/11-2016/v1/Samuel-mulburn.jpg', '/static/upload/images/teacher/11-2016/v1/Samuel-mulburn.jpg', 'Hello. My name is Samuel. I am from America.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I joined ILA after completing my CELTA. I aim to make my classes fun, challenging and interactive. I teach English to a range of levels and ages in a fun and friendly manner.</p>\r\n', '', '1', '2016-11-14 11:25:28');
INSERT INTO `teacher` VALUES ('525', 'Oliver', 'Hix', 'oliver-hix', '/static/upload/images/teacher/11-2016/v2/Oliver-Hix.jpg', '/static/upload/images/teacher/11-2016/v2/Oliver-Hix.jpg', 'Hi, my name is Oliver. I am from America. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Young Learner Extension (ILA) B.A. [College of Charleston - U.S.A.]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I joined ILA after completing my CELTA I am looking forward to creating a fun environment in which students can practice their new language skills.</p>\r\n', '', '1', '2016-11-21 17:20:08');
INSERT INTO `teacher` VALUES ('526', 'Mark', 'Witzmann', 'mark-witzmann', '/static/upload/images/teacher/11-2016/v2/Mark-Witzmann.jpg', '/static/upload/images/teacher/11-2016/v2/Mark-Witzmann.jpg', 'Hi my name is Mark. I am from Cape Town, South Africa.', '', '<p>\r\n	TEFOL YOUNG LEARNERS TRAINING (ILA) BACHELOR OF ARTS BACHELOR OF LAWS ATTORNEY AND CONVEYANCER</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I joined ILA after completing my TEFOL. I have experience to date in teaching Vietnamese adults and teenagers. My teaching style is communicative, peer to peer with lots of fun, energy and a caring attitude. I love to instill knowledge in my classrooms.</p>\r\n', '', '1', '2016-11-21 17:23:28');
INSERT INTO `teacher` VALUES ('527', 'Colin', 'Lynch', 'colin-lynch', '/static/upload/images/teacher/11-2016/v2/Colin-Lynch.jpg', '/static/upload/images/teacher/11-2016/v2/Colin-Lynch.jpg', 'Hey Everybody! My name is Colin and I am from Chicago, IL USA.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Bachelor of Arts, History and Economics [Vanderbilt University, Nashville, TN, USA]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	After graduating university, I worked as a financial and accounting litigation consultant in Chicago and New York City for just over one year. After consulting, I completed my CELTA in October 2016 in Chicago. I then came to ILA in HCMC in November 2016. This will be my first full-time teaching position and I am excited to help more people learn English and more about Western culture. I plan to foster a fun and challenging environment in the classroom.</p>\r\n', '', '1', '2016-11-21 17:26:35');
INSERT INTO `teacher` VALUES ('528', 'Cynthia ', 'Corne', 'cynthia-corne', '/static/upload/images/teacher/11-2016/v2/Cindy-Corne.jpg', '/static/upload/images/teacher/11-2016/v2/Cindy-Corne.jpg', 'Hi, I am Cindy from New Zealand!', '', '<p>\r\n	- Advanced Certificate in Teaching English to Speakers of Other Languages (TESOL), specializing in teaching grammar. - BSc; Chemistry and Pharmacology (The University of Auckland, New Zealand)</p>\r\n', '', null, 'NZ', null, null, '<p>\r\n	I moved to Vietnam after completing my Bachelor of Science and TESOL certificate. I teach young learners as well as adults, and have a friendly, active teaching style. I love to create a fun and comfortable classroom environment, so that students can communicate effectively and work collaboratively.</p>\r\n', '', '1', '2016-11-21 17:28:36');
INSERT INTO `teacher` VALUES ('529', 'Liam', 'Hewson', 'liam-hewson', '/static/upload/images/teacher/11-2016/v2/Liam-Hewson.jpg', '/static/upload/images/teacher/11-2016/v2/Liam-Hewson.jpg', 'Hi my name is Liam. I am from Ireland', '', '<p>\r\n	TEFLINK Degree for teaching English Cambridge English: Grammar for Teachers - Language Awareness. Cambridge English: How to teach IELTS. Applied Social Science: Honours Bachelors degree (level 8). Institute of Technology Tralee - Ireland.</p>\r\n', '', null, 'IE', null, null, '<p>\r\n	I have been teaching English in Vietnam for over 2 years. Before this I was teaching and working with different community development projects in Ireland. I teach to all ages and various levels. My teaching is interactive with a friendly approach centered around personal student development.</p>\r\n', '', '1', '2016-11-21 17:29:28');
INSERT INTO `teacher` VALUES ('530', 'William', 'Fielding', 'william-fielding', '/static/upload/images/teacher/11-2016/v2/Will-Fielding.jpg', '/static/upload/images/teacher/11-2016/v2/Will-Fielding.jpg', 'Hi, my name is William and I am from the UK.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA). B.A. European Politics (University of Nottingham, UK).</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	After spending time volunteering teaching English to refugees, I decided to take my CELTA and pursue a career in ESL teaching. ILA is my first full time teaching position. I completed my CELTA in Summer 2016, and shortly afterwards found my first full time teaching position with ILA. I teach to all ages and a variety of levels, and work to create a learning environment where students are both comfortable and challenged.</p>\r\n', '', '1', '2016-11-21 17:31:10');
INSERT INTO `teacher` VALUES ('531', 'Jade ', 'Chittock', 'jade-chittock', '/static/upload/images/teacher/11-2016/v2/jade-Chittock.jpg', '/static/upload/images/teacher/11-2016/v2/jade-Chittock.jpg', 'Hi. My name is Jade. I am from England. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Young Learner Extension to the Certificate in Teaching English to Speakers of Other Languages (ILAYL) Bsc in Psychology (University of Kent-England)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined IlA after completing my CELTA. I teach young learners (ranging from 4 - 17 years old). My teaching style is an interactive one. I am very friendly and approachable, and ensure to make my lessons fun and engaging for all learners. I work to create a comfortable environment so that students feel comfortable to practice the new language.</p>\r\n', '', '1', '2016-11-21 17:32:18');
INSERT INTO `teacher` VALUES ('532', 'Bradley', 'Cormack', 'bradley-cormack', '/static/upload/images/teacher/11-2016/v2/Bradley-Cormack.jpg', '/static/upload/images/teacher/11-2016/v2/Bradley-Cormack.jpg', 'Hi. My name is Brad. I am from South Africa.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages ̣̣̣̣̣̣̣̣̣̣̣(CELTA) Young Learner Extension (ILAYL) M.B.A. Finance &amp; Investment (Rhodes University, South Africa) B.Sc. Chemistry Honours (Rhodes University, South Africa)</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I joined ILA after doing my CELTA in Ho Chi Minh City. I teach an array of classes and have a friendly approach in the classroom. I work to create a comfortable leaning environment for students and ensure that there is plenty opportunity for students to practise new language.</p>\r\n', '', '1', '2016-11-21 17:32:35');
INSERT INTO `teacher` VALUES ('533', 'Danielle', 'Lisnek', 'danielle-lisnek', '/static/upload/images/teacher/11-2016/v2/Damielle-Lisnek.jpg', '/static/upload/images/teacher/11-2016/v2/Damielle-Lisnek.jpg', 'Hi! My name is Danielle. I am from America (Chicago). ', '', '<p>\r\n	-Certificate in Teaching English as a Foreign Language (TEFL) with a focus in Teaching English to Young Learners (TEYL). -B.S. Human Development and Family Studies (Child Development) from The University of Wisconsin, Madison.</p>\r\n', '', null, 'US', null, null, '<p>\r\n	After graduating from university in 2015, I spent the past year in Zambia, Africa educating young people about reproductive health. Following my passion for children, education and travel, I moved to Vietnam to teach English to young learners at ILA. I have an interactive, student-centered teaching style and love to bring excitement and innovation to the classroom. I am friendly and approachable and strive to make students feel confident in themselves and their abilities.</p>\r\n', '', '1', '2016-11-21 17:34:06');
INSERT INTO `teacher` VALUES ('534', 'Richard', 'Gove', 'richard-gove', '/static/upload/images/teacher/11-2016/v2/Richard-Gove.jpg', '/static/upload/images/teacher/11-2016/v2/Richard-Gove.jpg', 'Hi my name is Richard. I am from Canada.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Young Learners Course through ILA (ILAYL) B.a. in Music (St. Francis Xavier University, Antigonish, Nova Scotia, Canada)</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	I have been teaching English since 2008. I taught guitar for many years before that. I teach many age levels including Jumpstart, young learners and general English to adult learners. My teaching style is interactive and fun. I like to create a fun and relaxed atmosphere in the classroom to facilitate learning to enhance the learning experience.</p>\r\n', '', '1', '2016-11-21 17:34:55');
INSERT INTO `teacher` VALUES ('535', 'Christopher', 'Hickey', 'christopher-hickey', '/static/upload/images/teacher/12-2016/ver1/Chris%20Hickey.jpg', '/static/upload/images/teacher/12-2016/ver1/Chris%20Hickey.jpg', 'Hi, my name is Chris. I am from England.', '', '<p>\r\n	Teaching English as a Foreign Language certificate (TEFL) B.A from the University of Reading</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have taught a students from a wide range of ages in the UK. I like to cultivate a relaxed but enthusiastic approach to learning in the classroom .</p>\r\n', '', '1', '2016-12-14 13:48:49');
INSERT INTO `teacher` VALUES ('537', 'Tracey', 'Sullivan', 'tracey-sullivan', '/static/upload/images/teacher/12-2016/ver1/Tracey%20Sullivan.jpg', '/static/upload/images/teacher/12-2016/ver1/Tracey%20Sullivan.jpg', 'Hi, I\\\'m Tracey from the United States.', '', '<p>\r\n	B.S. Arizona State University Teaching English as Foreign Language (TEFL)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	After receiving my Bachelor\\&#39;s Degree in Speech and Hearing Science, I worked as a volunteer English teacher in West Africa in 2010. In 2015 I worked as an English teacher in Hunan, China. I enjoy the enthusiasm involved in teaching young learners. I encourage my students to use and practice English everyday.</p>\r\n', '', '1', '2016-12-14 14:01:52');
INSERT INTO `teacher` VALUES ('538', 'Navneet', 'Kaur', 'navneet-kaur', '/static/upload/images/teacher/12-2016/ver1/Navneet%20Kaur.jpg', '/static/upload/images/teacher/12-2016/ver1/Navneet%20Kaur.jpg', 'Hello, My name is Navneet. I am from America.', '', '<p>\r\n	TEFL International TESOL Certification. B.S. in Technical Management.</p>\r\n', '', null, 'US', null, null, '<p>\r\n	In United States I have worked as a substitute teacher for three years. I have a friendly attitude and thoroughly enjoy teaching.</p>\r\n', '', '1', '2016-12-14 14:02:12');
INSERT INTO `teacher` VALUES ('539', 'Patricia', 'Harris-Seaton', 'patricia-harris-seaton', '/static/upload/images/teacher/12-2016/ver1/Patricia%20Seaton.jpg', '/static/upload/images/teacher/12-2016/ver1/Patricia%20Seaton.jpg', 'Hello Readers, My name\\\'s Patricia and I\\\'m from London, England.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Extension Certificate in Business English Extension Certificate in Equality &amp; Diversity but to name a few.</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching for over 10 years and I am still passionate about achieving the best outcomes for my students. I am friendly and. very approachab!e.</p>\r\n', '', '1', '2016-12-14 14:06:24');
INSERT INTO `teacher` VALUES ('540', 'Maggie', 'Teper', 'maggie-teper', '/static/upload/images/teacher/12-2016/ver1/Maggie%20Teper.jpg', '/static/upload/images/teacher/12-2016/ver1/Maggie%20Teper.jpg', 'Hello! My name is Maggie. I\\\'m from Chicago in the USA.', '', '<p>\r\n	&mdash;CELTA &mdash;Bachelor of Science (Winona State University - USA)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I have been living and teaching in Hanoi since August. My previous experiences with kids includes tutoring, mentoring, and volunteering of different sorts. I am passionate about creating a positive learning environment for my students and being creative in the classroom. In my free time I enjoy reading, singing, and being with friends.</p>\r\n', '', '1', '2016-12-14 17:56:35');
INSERT INTO `teacher` VALUES ('541', 'Carol', 'King', 'carol-king', '/static/upload/images/teacher/12-2016/ver2/carol-king2.jpg', '/static/upload/images/teacher/12-2016/ver2/carol-king2.jpg', 'Hi, my name is Carol. I\"m from Australia.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Bachelor of Nursing (Queensland, Australia)</p>\r\n', '', null, 'AU', null, null, '<p>\r\n	I joined ILA after completing my CELTA. At ILA I teach young learners. I aim to make all my classes fun and creative as well as making the students feel comfortable to practice the new language they have learnt.</p>\r\n', '', '1', '2016-12-30 10:46:13');
INSERT INTO `teacher` VALUES ('542', 'Sean', 'Jackson', 'sean-jackson', '/static/upload/images/teacher/2017/Jan/Sean-Jason.jpg', '/static/upload/images/teacher/2017/Jan/Sean-Jason.jpg', 'Hi, my name is Sean.  I am from the UK.', '', '<p>\r\n	Certificate in Teaching English as a Foreign Language (TEFL) University of Cambridge Teaching Knowledge Test - Young Learners (TKT-YL) B.A. (Hons) University of Sunderland, UK</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2012. Before moving to Vietnam I taught in China and Cambodia. I love teaching young learners as well as older students. My teaching style is very energetic with lots of games, songs and fun activities to help learners enjoy learning English.</p>\r\n', '', '1', '2017-01-03 15:07:43');
INSERT INTO `teacher` VALUES ('543', 'Mai', 'Newaishy', 'mai-newaishy', '/static/upload/images/teacher/2017/Jan/Mai-Newaishy.jpg', '/static/upload/images/teacher/2017/Jan/Mai-Newaishy.jpg', 'Hi! My name is Mai. I am from Egypt.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA)</p>\r\n', '', null, 'EG', null, null, '<p>\r\n	I joined ILA right after completing my CELTA. I have been teaching ESL for about 3 years, mostly in Egypt and briefly in Russia.</p>\r\n', '', '1', '2017-01-03 15:09:19');
INSERT INTO `teacher` VALUES ('544', 'Kiara', 'Burdine', 'kiara-burdine', '/static/upload/images/teacher/2017/Jan/Riara-Burdine.jpg', '/static/upload/images/teacher/2017/Jan/Riara-Burdine.jpg', 'Hi, My name is Kiara. I am from America.', '', '<p>\r\n	TEFL/TESOL Certificate 120 Hours B.A General Studies Minor in French</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I have taught English in South Korea and Hong Kong. I have taught kindergarten and up to ages 14. I like to have a fun and interactive classroom!</p>\r\n', '', '1', '2017-01-03 15:09:27');
INSERT INTO `teacher` VALUES ('545', 'Patrick', 'Hanlon', 'patrick-hanlon', '/static/upload/images/teacher/2017/Jan/Patrick-Hanlon.jpg', '/static/upload/images/teacher/2017/Jan/Patrick-Hanlon.jpg', 'Hello, My name is Patrick. You can call me Pat. I come from America.', '', '<p>\r\n	Certificate in Teaching English to Speakers of other Languages (CELTA) Young Learner Extension to the Certificate in Teaching English to Speakers of Other Languages (ILAYL) B.S. Biological Sciences [Eastern Illinois University - USA]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I started teaching English in Korea in 2012. From there, I moved to Taiwan and then moved back home to Chicago. While at home, I volunteer tutored adults. After a year at home, I decided to move to China and teach kindergarten students. In 2016, I joined ILA after completing my CELTA</p>\r\n', '', '1', '2017-01-03 15:10:15');
INSERT INTO `teacher` VALUES ('546', 'Adam', 'Smith', 'adam-smith', '/static/upload/images/teacher/2017/Jan/Adam-Smith.jpg', '/static/upload/images/teacher/2017/Jan/Adam-Smith.jpg', 'Hi! My name is Adam. I am from the United States of America.', '', '<p>\r\n	-TEFL Certificate from Florida State University\\&#39;s Center for Intensive English Studies (CIES) - B.S. Psychology from Florida State University - USA</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I have been teaching English as a foreign language since 2013. I joined ILA after teaching in South Korea and Turkey. My teaching style is to make sure all the students are actively participating in each lesson while also enjoying themselves.</p>\r\n', '', '1', '2017-01-03 15:11:47');
INSERT INTO `teacher` VALUES ('547', 'Jonathan', 'Hewitt', 'jonathan-hewitt', '/static/upload/images/teacher/2017/Jan/Jonathan-Hewitt.jpg', '/static/upload/images/teacher/2017/Jan/Jonathan-Hewitt.jpg', 'Hello. My name is Jonathan.  You can call me Jon.  I\\\'m from England.', '', '<p>\r\n	CELTA, St Giles, London, 2010 BA (Hons) English Literature, King\\&#39;s College, University of Cambridge MA (Merit) Theatre Practice, University of Exeter</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching TEFL since 2010. I have taught in schools in UK, China, Thailand, GCSE and other curricula I have helped students study for IELTS and TOIEC I have been studying to take DELTA</p>\r\n', '', '1', '2017-01-03 15:11:52');
INSERT INTO `teacher` VALUES ('548', 'Vuk', 'R.', 'vuk-r', '/static/upload/images/teacher/2017/Jan/Vuk-R.jpg', '/static/upload/images/teacher/2017/Jan/Vuk-R.jpg', 'Hi, I am Vuk and I am from Canada.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) Young Learner Extension to the Certificate in Teaching English to Speakers of Other Languages (YLECELTA) B.A. (Simon Fraser University, Canada)</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	I joined ILA after completing CELTA. I have a friendly rapport and create interactive lessons for my students.</p>\r\n', '', '1', '2017-01-03 15:13:20');
INSERT INTO `teacher` VALUES ('549', 'Valentina', 'Del Pino', 'valentina-del-pino', '/static/upload/images/teacher/2017/Jan/Velentina-Delpino.jpg', '/static/upload/images/teacher/2017/Jan/Velentina-Delpino.jpg', 'Hello! My name is Valentina. \r\nI am from America.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A. [Florida International University- U.S.A]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I taught Business English to adults in Bogota, Colombia after obtaining my CELTA certificate. I joined ILA to teach young learners and learn about Vietnam\\&#39;s culture. My teaching style is kinesthetic. I like to keep my students interested and motivated. It is very important to me that students feel comfortable and enjoy learning a language that is so important to their future.</p>\r\n', '', '1', '2017-01-03 15:15:06');
INSERT INTO `teacher` VALUES ('550', 'Liam', 'Casey', 'liam-casey', '/static/upload/images/teacher/2017/Jan/Liam-Casey.jpg', '/static/upload/images/teacher/2017/Jan/Liam-Casey.jpg', 'Hi! My name is Liam. I am from America. ', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) B.A. [University of Maryland - U.S.A.]</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I last taught English in Bogota, Colombia, the city where I obtained my CELTA. I taught Business English to adults. Now I teach young learners. I joined ILA not only because I wanted to teach in Vietnam, but also because I wanted to see and explore this beautiful country. My teaching style is highly interactive. I bring an energetic, friendly approach to learning. My goal is to have a fun, comfortable classroom where everyone can enjoy learning a new language together.</p>\r\n', '', '1', '2017-01-03 15:15:21');
INSERT INTO `teacher` VALUES ('551', 'Tom ', 'Simpson', 'tom-simpson', '/static/upload/images/teacher/2017/Jan/Tom-Simpson.jpg', '/static/upload/images/teacher/2017/Jan/Tom-Simpson.jpg', 'Hey, I\\\'m Tom, I recently moved to Vietnam from the UK.', '', '<p>\r\n	Certificate in Teaching English to speakers of other languages. BSc (with hons) in Mathematics, University of Liverpool. MSc in Mathematics, University of Liverpool.</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have recently finished university and decided to complete my CELTA in Vietnam. After finishing my CELTA I joined ILA. At the moment I am teaching young learners from the ages of four up to 18. I try to create a fun classroom environment in order to engage the learners as much as possible so they feel relaxed when learning a new language.</p>\r\n', '', '1', '2017-01-03 15:16:41');
INSERT INTO `teacher` VALUES ('552', 'Dan', 'Absolon', 'dan-absolon', '/static/upload/images/teacher/2017/Jan/Absolo.jpg', '/static/upload/images/teacher/2017/Jan/Absolo.jpg', 'Hi, my name is Dan. I\\\'m from Bath in England.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) BA in English Literature and Philosophy from the University of Aberdeen (1st)</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	Before joining ILA, I worked at summer schools in England and Israel. I find helping students learn rewarding, so hope to develop to become the best teacher I can. I hope to offer my students rich and enjoyable learning experiences.</p>\r\n', '', '1', '2017-01-03 15:16:52');
INSERT INTO `teacher` VALUES ('553', 'Emma', 'Rengel', 'emma-rengel', '/static/upload/images/teacher/2017/Jan/Emma-Rengel.jpg', '/static/upload/images/teacher/2017/Jan/Emma-Rengel.jpg', 'Hi. My name is Emma. I am from United States.', '', '<p>\r\n	- Certificate in Teaching English to Speakers of Other Languages (CELTA) - ILA Young Learner Extension to the Certificate in Teaching English to Speakers of Other Languages (ILAYL) -BA (Loyola Marymount University, Los Angeles California, USA)</p>\r\n', '', null, 'US', null, null, '<p>\r\n	I joined ILA after completing my CELTA. I teach young Learners including jumpstarts, super juniors, and smart teens. I am very excited to be teaching young learners in Vietnam. I have a very friendly approach to my teaching. I love to learn about my students and their culture. I love to have fun in the classroom.</p>\r\n', '', '1', '2017-01-03 15:17:53');
INSERT INTO `teacher` VALUES ('554', 'Ben', 'Whiteside', 'ben-whiteside', '/static/upload/images/teacher/2017/Jan/Ben-whiteside.jpg', '/static/upload/images/teacher/2017/Jan/Ben-whiteside.jpg', 'Hello, my name\\\'s Ben and I\\\'m from Ireland', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages ̣- CELTA B.A. Joint Hons in English and Philosophy from Queen\\&#39;s University Belfast</p>\r\n', '', null, 'IE', null, null, '<p>\r\n	I have been teaching English since 2012, in Hong Kong and Tokyo. I have experience teaching young learners and university students. I really enjoy teaching and I\\&#39;m excited about working in Vietnam</p>\r\n', '', '1', '2017-01-03 15:18:05');
INSERT INTO `teacher` VALUES ('555', 'Amber', 'Jay', 'amber-jay', '/static/upload/images/teacher/2017/Jan/Amber-Jay.jpg', '/static/upload/images/teacher/2017/Jan/Amber-Jay.jpg', 'Hello everyone! My name\\\'s Amber and I\\\'m from England. ', '', '<p>\r\n	- M.A in Applied Linguistics with Teaching English as a Second Language (TESOL). [University of Sheffield, UK. 2015] - Certificate in Teaching English to Speakers of Other Languages (CELTA) - ILA Young Learners Extension to the Certificate to the Certificate in Teaching English to Speakers of Other Language (ILAYL) - B.A in Psychology. [University of Sheffield, UK. 2011]</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I have been teaching English since 2008. I have taught English in England, Australia, The UAE and now Vietnam. I love teaching, and have a friendly and fun teaching style. It is important to me that I create a good atmosphere in my classroom, and that my students are always engaged in their learning. I am very excited to start work with ILA this year!</p>\r\n', '', '1', '2017-01-03 15:22:22');
INSERT INTO `teacher` VALUES ('556', 'Benai', 'Pham', 'benai-pham', '/static/upload/images/teacher/2017/Jan/Benna-Pham.jpg', '/static/upload/images/teacher/2017/Jan/Benna-Pham.jpg', 'Hello, my name is Benai. I am a Viet Kieu who grew up in New Zealand and Australia, and graduated with an anthropology degree in University California, Santa Cruz.', '', '<p>\r\n	TESOL certificate - private tuition, English for preschool, English for children.</p>\r\n', '', null, 'NZ', null, null, '<p>\r\n	I have 5+ years experience in teaching to a wide range of learners, including elementary school children, college students and disabled Vietnamese service workers.</p>\r\n', '', '1', '2017-01-03 15:22:37');
INSERT INTO `teacher` VALUES ('557', 'Oliver', 'Dani', 'oliver-dani', '/static/upload/images/teacher/2017/Jan/Oli-Dani.jpg', '/static/upload/images/teacher/2017/Jan/Oli-Dani.jpg', 'Hi my name is Oli I am from Cambridge, United Kingdom. I graduated in July with a degree in Psychology (International) from University of Leeds. I recently completed my CELTA in Vietnam.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA).</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA after completing my CELTA and I teach all Young Learner groups. I have a friendly, positive attitude to teaching, and I have experience working at 2 language schools in Cambridge, United Kingdom as an activities teacher. I have also volunteered teaching refugees English in Conversation classes in Leeds.</p>\r\n', '', '1', '2017-01-03 15:23:14');
INSERT INTO `teacher` VALUES ('558', 'Mark', 'Massey', 'mark-massey', '/static/upload/images/teacher/2017/Jan/Mark-Massey.jpg', '/static/upload/images/teacher/2017/Jan/Mark-Massey.jpg', 'Hello, my name is Mark. I\\\'m from England.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Languages (CELTA) BA Manchester Metropolitan University</p>\r\n', '', null, 'GB', null, null, '<p>\r\n	I joined ILA after completing my CELTA. I like to build up a rapport with my students to ensure that the classroom is a relaxed environment, where they can have fun while practicing a new language.</p>\r\n', '', '1', '2017-01-03 15:23:31');
INSERT INTO `teacher` VALUES ('559', 'Nari', 'McSkimming', 'nari-mcskimming', '/static/upload/images/teacher/2017/Jan/Nari-McSKimming.jpg', '/static/upload/images/teacher/2017/Jan/Nari-McSKimming.jpg', 'Hello, my name is Nari. I am from Perth, Australia.', '', '<p>\r\n	-Certificate III TESOL (Teaching English to Speakers of Other Languages, Perth, Australia) -Cambridge TKTYL (Teaching Knowledge Test: Young Learners, Wuxi, People\\&#39;s Republic of China) -Bachelor of Psychology Degree (Murdoch University, Perth, Australia)</p>\r\n', '', null, 'AU', null, null, '<p>\r\n	-My professional background is in the area of community and human services in both the public and private sectors. -I have been working as an ESL English Teacher since 2014 and joined ILA Vietnam in January 2017. -In the past I have taught very young learners from the age of 3 up to teenage students of age 15. -I have experience teaching English in both the private language center setting as well as in the public school setting -I always seek to create a positive and supportive learning environment in which students feel encouraged and comfortable to participate and engage to their full potential. I also hope to encourage positive and harmonious relationships among students and teaching staff and ensure that students feel comfortable and supported throughout the learning process.</p>\r\n', '', '1', '2017-01-03 15:25:14');
INSERT INTO `teacher` VALUES ('560', 'Andrea', 'Bex', 'andrea-bex', null, null, 'Hi. My name is Andi and I\\\'m from England.', null, 'Certificate in Teaching English to Speakers of Other Languages - CELTA\r\n\r\nYoung Learners Extension to the Certificate in Teaching English to Speakers of Other Languages - YLCELTA\r\n\r\nBA Hons Social Anthropology, University of Sussex, UK', null, null, 'GB', null, null, 'I worked as a University Student Advisor in Brighton, UK before moving to Vietnam\r\nI joined ILA after completing my CELTA with the company in Ho Chi Minh City. ', null, '0', '2017-01-03 15:26:51');
INSERT INTO `teacher` VALUES ('561', 'Chantal ', 'Cordeau', 'chantal-cordeau', null, null, 'Hi, my name is Chantal. I am from Canada.', null, 'Certificate in Teaching English to Speakers of Other Languages (CELTA)\r\n\r\nB.A. [University of Guelph - Canada]', null, null, 'CA', null, null, 'I have been teaching English since 2014.\r\n\r\nMy teaching style is very interactive. I like to create a fun and comfortable classroom environment, with plenty of time for students to practice speaking English.', null, '0', '2017-01-18 15:46:24');
INSERT INTO `teacher` VALUES ('562', 'Mallory', 'Harrigan', 'mallory-harrigan', '/static/upload/images/teacher/2017/Jan/Mallory-Harrigan.jpg', '/static/upload/images/teacher/2017/Jan/Mallory-Harrigan.jpg', 'Hello, my name is Mallory and I am from Canada.', '', '<p>\r\n	Certificate for Teaching English as a Foreign Launguage (TEFL) M.A. Community Psychology, Wilfrid Laurier University, Canada B.A. Psychology, Trent University, Canada</p>\r\n', '', null, 'CA', null, null, '<p>\r\n	I am an enthusiastic teacher to young learners. I work to ensure that every student reaches their full potential by encouraging students to creatively engage with subject matter. I am highly compassionate and take a student-centred approach to learning.</p>\r\n', '', '1', '2017-01-18 15:50:25');
INSERT INTO `teacher` VALUES ('563', 'Tanja', 'Nel', 'tanja-nel', '/static/upload/images/teacher/2017/Feb/Tanja-Nel.jpg', '/static/upload/images/teacher/2017/Feb/Tanja-Nel-thumb.jpg', 'Hi, my name is Tanja.I am from South Africa.', '', '<p>\r\n	Certificate in Teaching English to Speakers of Other Langauges (CELTA) MA in Foreign Language Teaching [University of Paris, FRANCE] B.A Honours [University of Johannesburg, South Africa] B. A [University of Johannesburg, South Africa]</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I have been teaching since 2o09. I completed my CELTA in 2009. I have taught languages in France and South Africa at various institutions ever since. I joined ILA in 2017. I believe that anyone can learn a foreign language. My teaching style is fun and interactive and I strive to create a safe learning enviroment for all my students. I encourage my students to express themselves and practice the language in different ways, both in and outside the classroom.</p>\r\n', '', '1', '2017-02-28 11:12:16');
INSERT INTO `teacher` VALUES ('564', 'Rodney', 'Sorensen', 'rodney-sorensen', null, null, 'Hi, My name is Rodney, I am from Australia.', null, '* Certificate in Teaching English as a Foreign Language\r\n* Bachelor of Laws / Bachelor of Commerce (Griffith University, Australia)', null, null, 'AU', null, null, 'I have just come to Vietnam and I am excited to be teaching English.\r\n\r\nI teach learners including Super Juniors and Jumpstarts.\r\n\r\nMy teaching style is student centred and focuses on creatively teaching the material through games and activities. I like to make the students feel confident and comfortable in the classroom with the focus being to have a go.', null, '0', '2017-03-01 15:37:51');
INSERT INTO `teacher` VALUES ('565', 'Belinda', 'Sorensen', 'belinda-sorensen', null, null, 'Hi, my name is Belinda. I am from Australia. ', null, 'Certificate in Teaching English as a Foreign Languages.\r\n\r\nBachelor of Laws.\r\n\r\nBachelor of Commerce.', null, null, 'AU', null, null, 'I am new to Vietnam and excited to be teaching English. \r\n\r\nI teach Super Juniors.  My teaching style is student centered. Learning English is easy if the children are having fun.', null, '0', '2017-03-01 15:43:15');
INSERT INTO `teacher` VALUES ('566', 'Jeff', 'Byron', 'jeff-byron', '/static/upload/images/teacher/2017/march/JeffByron.jpg', '/static/upload/images/teacher/2017/march/JeffByron-thumb.jpg', 'Hello, my name is Jeff and I am from Australia.', '', '<p>\r\n	Certificate IV in Teaching English to Speakers of other Languages (TESOL) Bachelor of Adult and Vocational Education</p>\r\n', '', null, 'AU', null, null, '<p>\r\n	I have been teaching in Australia for many years and have recently moved to Vietnam. I enjoy teaching learners of all ages, from young learners to adults. I believe that a friendly, comfortable learning space is essential for success, where students feel motivated to practice new language.</p>\r\n', '', '1', '2017-03-17 15:01:25');
INSERT INTO `teacher` VALUES ('567', 'Sarah', 'Dullaghan', 'sarah-dullaghan', '/static/upload/images/teacher/2017/march/SarahDullaghan.jpg', '/static/upload/images/teacher/2017/march/SarahDullaghan-thumb.jpg', 'Hello! I\\\'m Sarah, and I am from Wales in the UK.', '', '<p>\r\n	Certificate IV Teaching English to Speakers of Other Languages (TESOL) ILA\\&#39;s YLCELTA BA (Hons) - University of the Arts, London</p>\r\n', '', null, 'WA', null, null, '<p>\r\n	I have taught Creative Learning in the UK for three years, using singing, dancing and drama to aid children\\&#39;s learning. I use my theatrical training and background to create fun, engaging ESL lessons for all students. I currently teach Super Juniors.</p>\r\n', '', '1', '2017-03-20 11:23:04');
INSERT INTO `teacher` VALUES ('568', 'Tom', 'Liebenberg', 'tom-liebenberg', '/static/upload/images/teacher/2017/march/TomLiebenberg.jpg', '/static/upload/images/teacher/2017/march/TomLiebenberg-thumb.jpg', 'Hi there, my name is Tom and I\\\'m all the way from Johannesburg, South Africa. ', '', '<p>\r\n	~ Certificate in Teaching English to Speakers of Other Languages (TESOL) ~ ILA Young Learners (ILAYL) ~ B.Com (Bachelor of Commerce - University of South Africa) ~ Postgraduate Degree (International Development - University of London)</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I joined ILA after completing the TESOL course in HCMC. I enjoy teaching and like to make learning as interactive and engaging as possible. I also have a passion for international development and international affairs.</p>\r\n', '', '1', '2017-03-20 11:27:10');
INSERT INTO `teacher` VALUES ('569', 'Jermaine ', 'Gemmell', 'jermaine-gemmell', '/static/upload/images/teacher/2017/march/JermaineGemmel.jpg', '/static/upload/images/teacher/2017/march/JermaineGemmel-thumb.jpg', 'Hi, my name is Jermaine and I am from South Africa.', '', '<p>\r\n	-Certificate in Teaching English to Speakers of Other Languages ( TESOL) -Young learner extension to TESOL certificate -Btech in Public Relations Management</p>\r\n', '', null, 'ZA', null, null, '<p>\r\n	I\\&#39;ve always wanted to live abroad but in the past 2 years- through my job-I\\&#39;ve developed a passion for teaching.</p>\r\n', '', '1', '2017-03-20 11:30:39');
